import React, {useState} from 'react';
import Word from './Word';
import './App.css';
//import TimePicker from 'react-time-picker';

function App() {
  const numToWord = {1: 'ONE', 2: 'TWO', 3:'THREE', 4:'FOUR', 5:'FIVE', 6:'SIX', 7:'SEVEN', 8:'EIGHT', 9:'NINE', 10:'TEN', 11:'ELEVEN', 12:'TWELVE'};

  const wordsStart = ['IT', 'IS', 'TEN', 'TWENTY', 'FIVE', 'QUARTER', 'HALF', 'PAST', 'TO'];
  const wordsEnd = ['ONE', 'TWO', 'THREE', 'FOUR', 'FIVE', 'SIX', 'SEVEN', 'EIGHT', 'NINE', 'TEN', 'ELEVEN', 'TWELVE', 'O\'CLOCK'];
  const words = wordsStart.concat(wordsEnd);

  // let wordsLit = words.map(curWord => {
  //   var wordState = wordMap.get(curWord);
  //   return {word: curWord, lit: wordState};
  // });

  // initialize states of words
  let wordsStartMap = new Map();
  let wordsEndMap = new Map();
  wordsStart.forEach(word => wordsStartMap.set(word, false));
  wordsEnd.forEach(word => wordsEndMap.set(word, false));

  var d = new Date();
  var hour = d.getHours();
  var minutes = d.getMinutes();
  const [value, onChange] = useState('10:00');

  var m = minutes;
  if (m > 30) {
    m = 60 - m;
  }

  if (hour > 12) {
    hour -= 12;
  }
  light(wordsEndMap, numToWord[hour]); // fix

  // it is o'clock
  light(wordsStartMap, "IT");
  light(wordsStartMap, "IS");
  light(wordsEndMap, "O'CLOCK");

  if (28 <= minutes && minutes < 33) {
    // SPECIAL CASE --> no half to
    light(wordsStartMap, "HALF");
    light(wordsStartMap, "PAST");
    console.log("half past");
  } else if (3 <= m && m < 8) {
    light(wordsStartMap, "FIVE");
    console.log("five");
  } else if (8 <= m && m < 13) {
    light(wordsStartMap, "TEN");
    console.log("ten");
  } else if (13 <= m && 18) {
    light(wordsStartMap, "QUARTER");
    console.log("quarter");
  } else if (18 <= m && m < 23) {
    light(wordsStartMap, "TWENTY");
    console.log("twenty");
  } else if (23 <= m && m < 28) {
    light(wordsStartMap, "TWENTY");
    light(wordsStartMap, "FIVE");
    console.log("twenty five");
  }

  if (3 <= minutes && minutes < 30) {
    light(wordsStartMap, "PAST");
    console.log("past");
  } else if (30 < minutes && minutes <= 57) {
    light(wordsStartMap, "TO");
    console.log("to");
  }

  function light(map, word) {
    map.set(word, true);
  }

  let wordMap = new Map([...wordsStartMap, ...wordsEndMap]);
  console.log(wordsStartMap);
  console.log(wordMap);

  //const [wordStates, setWordStates] = useState([]);
  //setWordStates(wordsLit); // array of booleans

  return (
    <div className="App">

      <div>
        <h3 className="title">WORD CLOCK</h3>
      </div>

      <div className="flex-container">
        {words.map(curWord => (
          <Word text={curWord} lit={wordMap.get(curWord)}/>
        ))}
      </div>

      <div>
        {/* <TimePicker onChange={onChange} value={value}/> */}
      </div>

    </div>
  );
}

export default App;
