    // half past
  }

  if (m < 30) {
    // past
  } else if (m > 30) {
    // to
  }


  //const [wordStates, setWordStates] = useState([]);
  //setWordStates(wordsLit); // array of booleans

  return (
    <div className="App">

      <div>
        <h3 className="title">WORD CLOCK</h3>
      </div>

      <div className="flex-container">
        {words.map(curWord => (
          <Word text={curWord} lit={wordMap.get(curWord)}/>
        ))}
      </div>

    </div>
  );
}

export default App;
